      out.write("\t// validation func\r\n");
      out.write("\tfunction validate(exp, value) {\r\n");
      out.write("\t\tvar regid = /^[A-za-z]{6,15}/g;\r\n");
      out.write("\t\tvar regpw = /^(?=.*[a-zA-Z])(?=.*[!@#$%^*+=-])(?=.*[0-9]).{8,25}$/;\r\n");
      out.write("\t\tvar regemail = /([\\w-\\.]+)@((\\[[0-9]{1,3}\\.[0-9]{1,3}\\.[0-9]{1,3}\\.)|(([\\w-]+\\.)+))([a-zA-Z]{2,4}|[0-9]{1,3})(\\]?)$/;\r\n");
      out.write("\r\n");
      out.write("\t\t// id\r\n");
      out.write("\t\tif (exp == \"id\") {\r\n");
      out.write("\t\t\tif (regid.test(value)) {\r\n");
      out.write("\t\t\t\treturn true;\r\n");
      out.write("\t\t\t} else {\r\n");
      out.write("\t\t\t\treturn false;\r\n");
      out.write("\t\t\t}\r\n");
      out.write("\t\t\t// pw\r\n");
      out.write("\t\t} else if (exp == \"pw\") {\r\n");
      out.write("\t\t\tif (regpw.test(value)) {\r\n");
      out.write("\t\t\t\treturn true;\r\n");
      out.write("\t\t\t} else {\r\n");
      out.write("\t\t\t\treturn false;\r\n");
      out.write("\t\t\t}\r\n");
      out.write("\t\t} else if (exp == \"email\") {\r\n");
      out.write("\t\t\tif (regemail.test(value)) {\r\n");
      out.write("\t\t\t\treturn true;\r\n");
      out.write("\t\t\t} else {\r\n");
      out.write("\t\t\t\treturn false;\r\n");
      out.write("\t\t\t}\r\n");
      out.write("\t\t}\r\n");
      out.write("\t}\r\n");
